css:
@page {
            size: A4;
            margin: 0;
        }

        body {
            margin: 1.4cm 0; /* Add margin to the body to create space at the top and bottom */
        }

        table {
            width: 100%;
            font-size: 11px;
            margin-top: 1.4cm;
        }

        td {
            width: 6.4cm;
            height: 3.8cm;
        }

        td div {
            margin-left: 0px;
            margin-top: 0px;
        }

        td div:first-child {
            font-size: 12px;
        }
